    private void b1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b1ActionPerformed
       double basic;
       double DA,HRA,others=0,earning;
       basic = Double.parseDouble(t1.getText());
       if(p1.isSelected())
       {
           switch (cb1.getSelectedIndex()) {
               case 1:
                   DA = 0.50*basic;
                   break;
               case 2:
                   DA = 0.60*basic;
                   break;
               case 3:
                   DA = 0.80*basic;
                   break;   
               case 4:
                   DA = 0.90*basic;
                   break;
               default:
                   DA = 0;
                   break;
           }
       }
       else
           DA = 1000;
       
       if(r1.isSelected())
           HRA = 4000;
       else if(u1.isSelected())
           HRA = 8000;
       else
           HRA = 0;
          
       
       if(jc1.isSelected())
           others = others + 3000;
       if(jc2.isSelected())
           others = others + 1000;
       if(jc3.isSelected())
           others = others + 2000;
       if(jc4.isSelected())
           others = others + 1500;
       if(jc5.isSelected())
           others = others + 500;
       
       earning = DA + HRA + others;
       t2.setText(Double.toString(DA));
       t3.setText(Double.toString(HRA));
       t4.setText(Double.toString(others));
       t5.setText(Double.toString(earning));
       bt2.setEnabled(true);
    }//GEN-LAST:event_b1ActionPerformed

