    private void bt2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bt2ActionPerformed
        double basicDA;
        double DA,IT,PF=0,Social=0,det;
        DA = Double.parseDouble(t2.getText());
        basicDA = Double.parseDouble(t1.getText())+DA;
        
        if(p1.isSelected())
        {
            if(basicDA >= 30000)
                IT =0.30 * basicDA;
            else if(basicDA >= 15000)
                IT =0.20 * basicDA;
            else 
                IT =0.10 * basicDA;
            PF =0.10 * basicDA;
            Social =2000;
        }
        else
            IT =1000;
            det = IT + PF + Social;
            t6.setText(Double.toString(IT));
            t7.setText(Double.toString(PF));
            t8.setText(Double.toString(Social));
            t9.setText(Double.toString(det));
            bt3.setEnabled(true);
    }//GEN-LAST:event_bt2ActionPerformed

    private void bt3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bt3ActionPerformed
        double basic,earning,det,inhand;
        basic = Double.parseDouble(t1.getText());
        earning = Double.parseDouble(t5.getText());
        det = Double.parseDouble(t9.getText());
        inhand = basic + earning - det;
        t10.setText(Double.toString(inhand));
    }//GEN-LAST:event_bt3ActionPerformed

